mapper.insert:
insert into logger (
  name, created, last_modified, ip, yaml_str
)values (
  #{name}, #{created}, #{lastModified}, #{ip}, #{yamlStr}
)

mapper.update:
update logger
set
    status = #{status}
where
    id = #{id}

mapper.insert:
insert into logger_hist (
  logger_id, ip, last_modified, msg
)values (
  #{loggerId}, #{ip}, #{lastModified}, #{msg}
)
